import React, { Component } from 'react'
import { useState, useEffect } from 'react';
import axios from 'axios';
import '../App.css';

function Home() {

     // setup state
  const [recipes, setRecipes] = useState([]);
  const [error, setError] = useState("");
  const [name, setName] = useState("");
  const [author, setAuthor] = useState("");
  const [quantity, setQuantity] = useState();
  const [instruction, setInstruction] = useState("");

  const fetchRecipes = async() => {
    try {      
      const response = await axios.get("/user/recipes");
      setRecipes(response.data.recipes);
    } catch(error) {
      setError("error retrieving recipes: " + error);
    }
  }
  const createRecipe = async() => {
    try {
      await axios.post("/user/recipes", {name: name, author: author, quantity: quantity, instruction: instruction});
    } catch(error) {
      setError("error adding a recipe: " + error);
    }
  }
  const deleteOneRecipe = async(recipe) => {
    try {
      await axios.delete("/user/recipes/" + recipe.id);
    } catch(error) {
      setError("error deleting a recipe" + error);
    }
  }

  // fetch ticket data
  useEffect(() => {
    fetchRecipes();
  },[]);

  const addRecipe = async(e) => {
    e.preventDefault();
    await createRecipe();
    fetchRecipes();
    setName("");
    setAuthor("");
    setQuantity();
    setInstruction("");
  }

  const deleteRecipe = async(recipe) => {
    await deleteOneRecipe(recipe);
    fetchRecipes();
  }

  //const loggedIn = this.props.loggedIn;

  // render results
  return (
    <div className="App">
      {error}
            <h1>Create a Recipe</h1>
            <form onSubmit={addRecipe}>
              <div>
                <label>
                  Name:
                  <input type="text" value={name} onChange={e => setName(e.target.value)} />
                </label>
              </div>
              <div>
                <label>
                  By:
                  <input type="text" value={author} onChange={e => setAuthor(e.target.value)} />
                </label>
              </div>
              <div>
                <label>
                  Quantity:
                  <input type="number" value={quantity} onChange={e => setQuantity(parseInt(e.target.value))} />
                </label>
              </div>
              <div>
                <label>
                  Instructions:
                  <textarea value={instruction} onChange={e => setInstruction(e.target.value)}></textarea>
                </label>
              </div>
              <input type="submit" value="Submit" />
            </form>
            <h1>Recipes</h1>
            {recipes.map( recipe => (
              <div key={recipe.id} className="recipe">
                <div className="instruction">
                  <p>{recipe.name}&emsp;{recipe.quantity}&emsp;{recipe.author}</p>
                  <p><i>{recipe.instruciton}</i></p>
                </div>
                <button onClick={e => deleteRecipe(recipe)}>Delete</button>
              </div>
            ))} 
    </div>
  );
}

export default Home
